import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios'
const Detail = () => {

    const {id}=useParams();
   
    const [employee,setEmployee]=useState();

    useEffect(()=>{
        axios.get(`http://localhost:8000/detail/${id}`)
        .then(res=>setEmployee(res.data))
    })
    return (
        <div className="min-h-screen bg-gray-100">
            {/* Cover Photo */}
            <div className="w-full h-64">
                <img
                    className="w-full h-full object-cover"
                    
                    alt="Cover"
                />
            </div>

            {/* Profile Section */}
            <div className="flex justify-center -mt-16">
                <img
                    className="w-32 h-32 rounded-full border-4 border-white object-cover"
                   
                    alt={employee.name}
                />
            </div>

            {/* Employee Details Section */}
            <div className="max-w-4xl mx-auto bg-white shadow-lg rounded-lg p-6 mt-6">
                {/* Name and Title */}
                <div className="text-center">
                    <h1 className="text-3xl font-bold">{employee.name}</h1>
                    <p className="text-gray-600">{employee.title}</p>
                </div>

                {/* Status */}
                {/*<div className="flex justify-center mt-4">
                    <span
                        className={`px-4 py-2 rounded-full text-white ${
                            employee.status === 'Active' ? 'bg-green-500' : 'bg-red-500'
                        }`}
                    >
                        {employee.status}
                    </span>
                </div>
                */}

                {/* Employee Info Grid */}
                <div className="mt-8 grid grid-cols-1 sm:grid-cols-2 gap-6">
                    {/* Joining Date */}
                    <div>
                        <h3 className="text-lg font-semibold">Joining Date</h3>
                        <p className="text-gray-700">{employee.joiningDate}</p>
                    </div>

                    {/* Address */}
                    <div>
                        <h3 className="text-lg font-semibold">Address</h3>
                        <p className="text-gray-700">{employee.address}</p>
                    </div>

                    {/* Blood Group */}
                    <div>
                        <h3 className="text-lg font-semibold">Blood Group</h3>
                        <p className="text-gray-700">{employee.bloodGroup}</p>
                    </div>

                    {/* Contact */}
                    <div>
                        <h3 className="text-lg font-semibold">Contact</h3>
                        <p className="text-gray-700">{employee.contact}</p>
                    </div>

                    {/* Role */}
                    <div>
                        <h3 className="text-lg font-semibold">Role</h3>
                        <p className="text-gray-700">{employee.role}</p>
                    </div>

                    {/* Email */}
                    <div>
                        <h3 className="text-lg font-semibold">Email</h3>
                        <p className="text-gray-700">{employee.email}</p>
                    </div>

                    {/* Total Leave 
                    <div>
                        <h3 className="text-lg font-semibold">Total Leave</h3>
                        <p className="text-gray-700">{employee.totalLeave} days</p>
                    </div>
                    */}

                    {/* Remaining Leave 
                    <div>
                        <h3 className="text-lg font-semibold">Remaining Leave</h3>
                        <p className="text-gray-700">{employee.remainingLeave} days</p>
                    </div>
                    */}
                </div>

                {/* Bio 
                <div className="mt-8">
                    <h3 className="text-lg font-semibold">Bio</h3>
                    <p className="text-gray-700 mt-2">{employee.bio}</p>
                </div>
                */}
            </div>
        </div>
    );
};

export default Detail;
