import React, { useEffect, useState } from 'react'
import axios from 'axios'
function AllApplications() {
 const [data,setData]=useState([])

 useEffect(()=>{
   axios.get('http://localhost:8000/allapplication')
   .then(res=>setData(res.data))
 },[])
  return (
    <div className="flex flex-wrap justify-center">
    {data.map((item) => (
         <div className="max-w-sm rounded overflow-hidden shadow-lg bg-white m-4 p-6">
         <h2 className="text-xl font-semibold">{item.name}</h2>
         <p className="text-gray-700">{item.description}</p>
         <p className="text-gray-600">{item.email}</p>
         <button className='btn-1 pl-4 pr-4 pt-1 pb-1 border-2 m-6'>Approve</button>
         <button className='btn-1 pl-4 pr-4 pt-1 pb-1 border-2 m-6'>Reject</button>
     </div>
    ))}
</div>
  )
}

export default AllApplications
