import React, { useEffect, useState } from 'react';
import axios from 'axios'


const TaskList = () => {
const [tasks,setTask]=useState([])
const id=localStorage.getItem('id')
useEffect(()=>{
  axios.get(`http://localhost:8000/gettask/${id}`)
  .then(res=>setTask(res.data))
},[,tasks])

const handleStatusChange=async (uid,isTrue)=>{
  await axios.put(`http://localhost:8000/taskcomplete/${uid}`,{status:!isTrue})
}
  return (
    <div className="max-w-2xl mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Assigned Tasks</h2>
      <ul className="space-y-4">
        {tasks.map((task) => (
          <li key={task.id} className="bg-white shadow-md rounded-lg p-4 flex justify-between items-center">
            <div>
            <label>
        <input 
          type="checkbox"
          onChange={()=>handleStatusChange(task.uid,task.status)}
          checked={task.status}
        />
       <h3>{task.status}</h3>
       <p className='text-green-300'>{task.status ? <p>Done</p> : <p className='text-color-red'>Pending</p>}</p>
      </label>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
