  console.log(isTrue)
  axios.put(`http://localhost:8000/taskcomplete/${id}`,{status:!isTrue})
}*/
  return (
    <div className="max-w-2xl mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Assigned Tasks</h2>
      <ul className="space-y-4">
        {tasks.map((task) => (
          <li key={task.id} className="bg-white shadow-md rounded-lg p-4 flex justify-between items-center">
            <div>
            <label>
        <input 
          type="checkbox"
          /*onChange={handleStatusChange(task.uid,task.status)}
          checked={task.status}*/
        />
       {task.data}
      </label>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
