import React, { useEffect, useState } from 'react';
import axios from 'axios'


const TaskList = () => {
const [tasks,setTask]=useState([])
const id=localStorage.getItem('id')
useEffect(()=>{
  axios.get(`http://localhost:8000/gettask/${id}`)
  .then(res=>console.log(res.data))
  .then(res=>setTask(res.data))
},[])
  return (
    <div className="max-w-2xl mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Assigned Tasks</h2>
      <ul className="space-y-4">
        {tasks.map((task) => (
          <li key={task.id} className="bg-white shadow-md rounded-lg p-4 flex justify-between items-center">
            <div>
              <h3 className="text-lg font-bold">{task.title}</h3>
              <p className="text-gray-600">{task.description}</p>
            </div>
            <span
              className={`inline-block px-2 py-1 text-sm font-semibold rounded-full 
                ${task.status === 'Completed' ? 'bg-green-200 text-green-800' : task.status === 'In Progress' ? 'bg-yellow-200 text-yellow-800' : 'bg-blue-200 text-blue-800'}`}
            >
              {task.status}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
