import React from 'react';

const NewsAndNotices = () => {
  // Sample data for news and notices
  const news = [
    {
      id: 1,
      title: 'React 18 Released!',
      description: 'React 18 brings concurrent rendering and automatic batching for better performance.',
      date: '2024-10-01',
    },
    {
      id: 2,
      title: 'Tailwind CSS v3.2 Now Available',
      description: 'The new version of Tailwind CSS offers even more flexibility and utility classes.',
      date: '2024-09-20',
    },
  ];

  const notices = [
    {
      id: 1,
      title: 'Maintenance Notice',
      description: 'The website will undergo maintenance on Saturday from 2 AM to 4 AM.',
      date: '2024-10-05',
    },
    {
      id: 2,
      title: 'Policy Update',
      description: 'We have updated our privacy policy to enhance user data protection.',
      date: '2024-09-28',
    },
  ];

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">News & Notices</h1>

      {/* News Section */}
      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Latest News</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {news.map((item) => (
            <div key={item.id} className="bg-white shadow-md rounded-lg p-4 border border-gray-200">
              <h3 className="text-xl font-semibold text-blue-600 mb-2">{item.title}</h3>
              <p className="text-gray-700 mb-2">{item.description}</p>
              <span className="text-gray-500 text-sm">{item.date}</span>
            </div>
          ))}
        </div>
      </section>

      {/* Notices Section */}
      <section>
        <h2 className="text-2xl font-semibold mb-4">Important Notices</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {notices.map((item) => (
            <div key={item.id} className="bg-white shadow-md rounded-lg p-4 border border-gray-200">
              <h3 className="text-xl font-semibold text-red-600 mb-2">{item.title}</h3>
              <p className="text-gray-700 mb-2">{item.description}</p>
              <span className="text-gray-500 text-sm">{item.date}</span>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default NewsAndNotices;
