import React, { useEffect, useState } from 'react';
import axios from 'axios'


const TaskList = () => {

function formatDate(date) {
    return date.toISOString().split("T")[0];
}

const day = new Date();
const fin=formatDate(day);
const [tasks,setTask]=useState([])
const id=localStorage.getItem('id')
useEffect(()=>{
  axios.get(`http://localhost:8000/gettask/${id}`)
  .then(res=>setTask(res.data))
},[tasks])

const handleStatusChange=async (uid,isTrue)=>{
  await axios.put(`http://localhost:8000/taskcomplete/${uid}`,{status:!isTrue})
}
  return (
    <div className="max-w-2xl mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Assigned Tasks</h2>
      <ul className="space-y-4">
        {tasks.map((task) => (
          task.date===fin && <li key={task.id} className="bg-white shadow-md rounded-lg p-4 flex justify-between items-center">
            <div>
            <label>
        <input 
          type="checkbox"
          onChange={()=>handleStatusChange(task.uid,task.status)}
          checked={task.status}
        />
       <h3>{task.data}</h3>
       {
        task.status ?<p className='text-lime-600'>Done</p> : <p className='text-red-600'>Pending</p>
       }
      </label>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
