import React, { useEffect, useState } from 'react'
import axios from 'axios'

import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);
function Performance() {

const labels = ['Saturday','Sunday','Monday','Tuesday','Wednesday','Thursday','Friday']; // X-axis labels
    

const id=localStorage.getItem('id')


const array=[];
const array1=[];

const [info,setInfo]=useState();
const [option,setOption]=useState();
const [data,setData]=useState([])
function formatDate(date) {
    return date.toISOString().split("T")[0];
  }

const date = new Date();
const day_1 = new Date(date); // Clone the date object
day_1.setDate(date.getDate() - 1);

const day_2 = new Date(date);
day_2.setDate(date.getDate() - 2);

const day_3 = new Date(date);
day_3.setDate(date.getDate() - 3);

const day_4 = new Date(date);
day_4.setDate(date.getDate() - 4);

const day_5 = new Date(date);
day_5.setDate(date.getDate() - 5);

const day_6 = new Date(date);
day_6.setDate(date.getDate() - 6);

const day_7 = new Date(date);
day_7.setDate(date.getDate() - 7);



const date_1=formatDate(day_1)
const date_2=formatDate(day_2)
const date_3=formatDate(day_3)
const date_4=formatDate(day_4)
const date_5=formatDate(day_5)
const date_6=formatDate(day_6)
const date_7=formatDate(day_7)


useEffect( ()=>{
   axios.get(`http://localhost:8000/gettask/${id}`)
  .then(res=>setData(res.data))
   
  }, []); 




useEffect(()=>{

    const filtered1= data.filter((item)=>{
        return item.date===date_1 && item.status
    }
    )
  
   array[0]=filtered1.length
   


    const filtered2= data.filter((item)=>{
        return item.date===date_2 && item.status
    }
    )

  
    array[1]=filtered2.length

    const filtered3=data && data.filter((item)=>{
        return item.date===date_3 && item.status
    }
    )

   
    array[2]=filtered3.length

    const filtered4=data && data.filter((item)=>{
        return item.date===date_4 && item.status
    }
    )

    
    array[3]=filtered4.length

    const filtered5=data && data.filter((item)=>{
        return item.date===date_5 && item.status
    }
    )

    
    array[4]=filtered5.length

    const filtered6=data && data.filter((item)=>{
        return item.date===date_6 && item.status
    }
    )

    
    array[5]=filtered6.length

    const filtered7=data && data.filter((item)=>{
        return item.date===date_7 && item.status
    }
    )

    array[6]=filtered7.length


console.log(array)


const filtered8= data.filter((item)=>{
    return item.date===date_1 
}
)
array1[0]=filtered8.length


const filtered9= data.filter((item)=>{
    return item.date===date_2
}
)
array1[1]=filtered9.length


const filtered10= data.filter((item)=>{
    return item.date===date_3
}
)
array1[2]=filtered10.length


const filtered11= data.filter((item)=>{
    return item.date===date_4 
}
)
array1[3]=filtered11.length



const filtered12= data.filter((item)=>{
    return item.date===date_5 
}
)
array1[4]=filtered12.length


const filtered13= data.filter((item)=>{
    return item.date===date_6 
}
)
array1[5]=filtered13.length


const filtered14= data.filter((item)=>{
    return item.date===date_7
}
)
array1[6]=filtered14.length

console.log(array1)
},[data])


useEffect(()=>{
    const info = {
        labels: labels,
        datasets: [
          {
            label: 'Total Task',
            data: array1,
            backgroundColor: 'rgba(75, 192, 192, 0.5)', // Color for Dataset 1
          },
          {
            label: 'Task Completed',
            data: array,
            backgroundColor: 'rgba(255, 99, 132, 0.5)', // Color for Dataset 2
          },
        ],
      };
    
      const options = {
        responsive: true,
        plugins: {
          legend: {
            position: 'top', // Position of the legend
          },
          title: {
            display: true,
            text: 'Bar Chart Example', // Chart title
          },
        },
      };

      setInfo(info)
      setOption(option)
},[array,array1])

  return (
    <div>
      <h1>This is performance page</h1>
      return <Bar data={info} options={option} />;
    </div>
  )
}

export default Performance
